#!/usr/bin/env pythonfrom setuptools import setup, find_packagesimport osfrom pathlib import Pathhere = Path(__file__).parent.absolute()# Get the long description from the README filewith open(here.joinpath('README.md'), encoding='utf-8') as f:    long_description = f.read()    setup(            name='paratc',            version='0.1.0',            description='',            long_description=long_description,            long_description_content_type="text/markdown",            url='',            author='',            author_email='TBD',            license='TBD',            packages=find_packages(where = 'src'),        package_dir={"": "src"})